import{Component,OnInit} from '@angular/core';
import{UserService} from '../services/user.service';
import { Router } from "@angular/router";
import { HttpClient,HttpHeaders } from "@angular/common/http";
import { FormGroup, FormBuilder, Validators} from "@angular/forms";
@Component({
    selector:'app-login',
    templateUrl:'./login.component.html',
    styleUrls:['./login.component.css']
})

export class LoginComponent implements OnInit{
    
    usernameOremail:string="";
    pswrd:string="";
    id:number=0;
    loginForm:FormGroup;
    constructor(private userService: UserService,private fb: FormBuilder,private router:Router,private http: HttpClient) {
        this.loginForm = this.fb.group({
            username: ["", [Validators.required, Validators.minLength(5)]],
            pswrd: ["", [Validators.required,Validators.minLength(8)]]
        });
    }

    login(): void {
        const user = { usernameOremail: this.usernameOremail, pswrd: this.pswrd };
        const httpOptions = {
            headers: new HttpHeaders({
              'Content-Type': 'application/json',
            }),
          };
        this.http.post('http://localhost:8080/auth/login', user,httpOptions).subscribe(
          (response: any) => {
            console.log(response);
            localStorage.setItem('loggedInUser', JSON.stringify(response));
            this.router.navigate(['/landing']);
          },
          (error) => {
            if (error.status === 400) {
              alert('Invalid credentials');
            } else {
                alert('Server unreachable');
            }
          }
        );
      }

    ngOnInit(): void {
    }

    
}

  
<div class="login-container">
    <div id="card">
        <div id="card-content">
            <div id="card-title">
                <h2>LOGIN</h2>
                <div class="underline-title"></div>
            </div>
            <form [formGroup]="loginForm" >
                <label for="usernaem" style="padding-top:13px">
                    &nbsp;Username/Email
                </label>
                <input id="username" formControlName="usernameOremail" class="form-content" type="text" name="username" autocomplete="on" required 
                [(ngModel)]="usernameOremail"/>
                <div class="form-border"></div>
                <div *ngIf="loginForm.get('usernameOremail')?.touched && loginForm.get('usernameOremail')?.hasError('required')" class="text-danger">
                    Username/Email is required.
                </div>
                <label for="user-password" style="padding-top:22px">&nbsp;Password
                </label>
                <input id="user-password" formControlName="pswrd" class="form-content" type="password" name="password" required
                [(ngModel)]="pswrd" />
                <div class="form-border"></div>
                <div *ngIf="loginForm.get('pswrd')?.touched && loginForm.get('pswrd')?.hasError('required')" class="text-danger">
                    Password is required.
                </div>
                <a href="#">
                    <legend id="forgot-pass">Forgot password?</legend>
                </a>
                <input id="login-btn" type="submit" name="submit" value="LOGIN" 
                    class="mx-auto d-block btn btn-warning" (click)="login()"/>
                <div class="my-4">
                <a routerLink="/signup" id="signup">Don't have an account yet?
                    <button class="btn btn-primary btn-small ml-3">Signup</button></a>
                </div>
            </form>
        </div>
    </div>
</div>
