import{Component,OnInit} from '@angular/core';
import{UserService} from '../services/user.service';
import { IUser } from "../services/IUser";
import { Router } from "@angular/router";
import { FormGroup, FormBuilder, Validators,AbstractControl,ValidationErrors } from "@angular/forms";
@Component({
    selector:'app-login',
    templateUrl:'./login.component.html',
    styleUrls:['./login.component.css']
})
export class LoginComponent implements OnInit{
    username:string="";
    password:string="";
    loginForm:FormGroup;
    constructor(private userService: UserService,private fb: FormBuilder,private router:Router) {
        this.loginForm = this.fb.group({
            username: ["", [Validators.required, Validators.minLength(5)]],
            password: ["", [Validators.required,Validators.minLength(8)]]
        });
    }

    ngOnInit(): void {
    }

    login(): void {
    if (this.loginForm.valid) {
      const username = this.loginForm.value.username;
      const password = this.loginForm.value.password;

      // Simulate async login validation using the asyncLoginValidator function
      this.asyncLoginValidator(username, password).then((isValid) => {
        if (isValid) {
          alert('Login successful');
          this.router.navigate(['/landing']);
        } else {
          alert('Invalid credentials');
          this.loginForm.setErrors({ invalidLogin: true });
        }
      });
    }
  }
  async asyncLoginValidator(username: string, password: string): Promise<boolean>  {
  
    // Simulate async login validation with a hardcoded username and password
    return new Promise<boolean>((resolve) => {
      setTimeout(() => {
        if (username === 'validUsername' && password === 'validPassword') {
            resolve(true); // Valid login
          } else {
            resolve(false); // Invalid login
          }
        }, 100); // Simulate a delay
    });
  }
}

  
<div class="login-container">
    <div id="card">
        <div id="card-content">
            <div id="card-title">
                <h2>LOGIN</h2>
                <div class="underline-title"></div>
            </div>
            <form [formGroup]="loginForm">
                <label for="usernaem" style="padding-top:13px">
                    &nbsp;Username
                </label>
                <input id="username" formControlName="username" class="form-content" type="text" name="username" autocomplete="on" required />
                <div *ngIf="loginForm.get('username')?.touched && loginForm.get('username')?.hasError('required')" class="text-danger">
                    Username is required.
                </div>
                <div class="form-border"></div>
                <label for="user-password" style="padding-top:22px">&nbsp;Password
                </label>
                <input id="user-password" formControlName="password" class="form-content" type="password" name="password" required />
                <div *ngIf="loginForm.get('password')?.touched && loginForm.get('password')?.hasError('required')" class="text-danger">
                    Password is required.
                </div>
                <div class="form-border"></div>
                <a href="#">
                    <legend id="forgot-pass">Forgot password?</legend>
                </a>
                <input id="login-btn" type="submit" name="submit" value="LOGIN" 
                    class="mx-auto d-block btn btn-warning" (click)="login()"/>
                <div class="my-4">
                <a routerLink="/signup" id="signup">Don't have an account yet?
                    <button class="btn btn-primary btn-small ml-3">Signup</button></a>
                </div>
            </form>
        </div>
    </div>
</div>
