import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { Observable,of } from "rxjs";
import { map } from "rxjs";
import { IUser } from "./IUser";

@Injectable({
    providedIn: "root"
})
export class UserService {
    constructor (private http: HttpClient) { }
    
    username:string='';
    protected Url = "http://localhost:8080/";

    addUser(user: IUser): Observable<IUser> {
        return this.http.post<IUser>(this.Url + "users", user)
        .pipe(map((response: any) =>response));
    }

    loggedinuser():Observable<{userid:number,username:string}|null>{
        const userJson = localStorage.getItem("loggedInUser");
        if (userJson) {
            const loggedInUser: IUser = JSON.parse(userJson);
            return of({userid:loggedInUser.userid,username:loggedInUser.username});
        }
        return of(null);
    }
}
