import{Component,OnInit} from "@angular/core";
import { CartService } from "../services/cart.services";
import { ICart } from "../services/ICart";
@Component({
    selector:"app-cart",
    templateUrl:"./cart.component.html",
    styleUrls:["./cart.component.css"]
})

export class CartComponent implements OnInit{
    cartitems:ICart[]=[];
    username:string='';
    totalcost:number=0;
    
    constructor(private cartService:CartService){}

    ngOnInit(){
        const loggedinuser = JSON.parse(localStorage.getItem('loggedInUser') || '{}');
        const userid = loggedinuser.userid;
        this.username = loggedinuser.username;
        if (userid) {
            this.cartService.getCartItems(userid).subscribe((data: ICart[]) => {
              this.cartitems = data;
              for (const item of this.cartitems) {
                this.totalcost += item.productprice;
              }
              console.log("Total Cost: ",this.totalcost);
            });
        }
    
    }
    remove(userid:number,productid:number):void{
        this.cartService.removeCartItem(userid,productid).subscribe(()=>{
            alert("Removed from cart successfully");
            window.location.reload();
        });
    }

    placeOrder(userid:number):void{
        this.cartService.placeOrder(userid).subscribe(()=>{
            alert("Order placed successfully");
            window.location.reload();
        });
    }
}
