import{Injectable} from '@angular/core';
import{HttpClient} from '@angular/common/http';
import { IProduct } from './IProduct';
import { UserService } from './user.service';
import { ICart } from './ICart';
import { Observable,of } from 'rxjs';
import { switchMap } from 'rxjs/operators';
@Injectable({
    providedIn:'root'
})
export class CartService{
    private url="http://localhost:8080";
    constructor(private http:HttpClient,private userservice: UserService){}
    userid:number=0;

    addtoCart(product: IProduct): Observable<any> {
        return this.userservice.loggedinuser().pipe(
            switchMap((user) => {
                if (user) {
                    const cartItem = {
                        userid: user.userid,
                        productid: product.productid,
                    };
                    console.log(cartItem);
                    return this.http.post<any>(`${this.url}/cart/add`, cartItem);
                } else {
                    return of(null); // Handle the case when the user is not logged in
                }
            })
        );
    }

    getCartItems(userid:number):Observable<ICart[]>{
        return this.http.get<ICart[]>(`${this.url}/cart/${userid}`);
    }

    removeCartItem(userid:number,productid:number):Observable<void>{
        return this.http.delete<any>(`${this.url}/cart/delete/${userid}/${productid}`);
    }

    placeOrder(userid:number):Observable<void>{
        return this.http.delete<any>(`${this.url}/cart/placeorder/${userid}`);
    }
}
