import{Component} from '@angular/core';
import{UserService} from '../services/user.service';
import { Router } from "@angular/router";
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { HttpClient, HttpHeaders } from "@angular/common/http";
@Component({
    selector:'app-login',
    templateUrl:'./login.component.html',
    styleUrls:['./login.component.css']
})
export class LoginComponent{
    loginForm:FormGroup;
    constructor(private userService: UserService,private fb: FormBuilder,private router:Router,
        private http: HttpClient) {
        this.loginForm = this.fb.group({
            username: ['', Validators.required],
            password: ['', Validators.required]
          });
     }

    onlogin(){
        const credentials = {
            username: this.loginForm.value.username,
            password: this.loginForm.value.password
          };
          const headers = new HttpHeaders({
            'Content-Type': 'application/json'
          });
      
          this.http.post<any>('http://localhost:8080/login', credentials, { headers }).subscribe(
            (response) => {
              console.log(response); // Handle successful login
            },
            (error) => {
              console.error(error); // Handle error
            }
          );
        
}
}
<div class="login-container">
    <div id="card">
        <div id="card-content">
            <div id="card-title">
                <h2>LOGIN</h2>
                <div class="underline-title"></div>
            </div>
            <form [formGroup]="loginForm" (ngSubmit)="onlogin()">
                <label for="user-email" style="padding-top:13px">
                    &nbsp;Username
                </label>
                <input id="username" formControlName="username" class="form-content" type="text" name="username" autocomplete="on" required />
                <div class="form-border"></div>
                <label for="user-password" style="padding-top:22px">&nbsp;Password
                </label>
                <input id="user-password" formControlName="password" class="form-content" type="password" name="password" required />
                <div class="form-border"></div>
                <a href="#">
                    <legend id="forgot-pass">Forgot password?</legend>
                </a>
                <input id="login-btn" type="submit" name="submit" value="LOGIN" 
                    class="mx-auto d-block btn btn-warning" (click)="onlogin()"/>
                <div class="my-4">
                <a routerLink="/signup" id="signup">Don't have an account yet?
                    <button class="btn btn-primary btn-small ml-3">Signup</button></a>
                </div>
            </form>
        </div>
    </div>
</div>
