import { Component,OnInit } from "@angular/core";
import { WishlistService } from "../services/wishlist.services";
import { IProduct } from "../services/IProduct";
import { IWish } from "../services/IWish";

@Component({
    selector: "app-wishlist",
    templateUrl: "./wishlist.component.html",
    styleUrls: ["./wishlist.component.css"]
})

export class WishlistComponent implements OnInit{
    username:string='';
    wishlistitems:IWish[]=[];
    constructor(private wishlistService:WishlistService){}

    ngOnInit(){
        const loggedinuser = JSON.parse(localStorage.getItem('loggedInUser') || '{}');
        const userid = loggedinuser.userid;
        this.username = loggedinuser.username;
        if (userid) {
            this.wishlistService.getWishlistItems(userid).subscribe((data:IWish[]) => {
              this.wishlistitems = data;
            });
        }
    }
    remove(userid:number,productid:number):void{
        this.wishlistService.removeWishlistItem(userid,productid).subscribe(()=>{
            alert("Removed from wishlist successfully");
            window.location.reload();
        });
    }
    
    addToCart(product:IProduct):void{
        this.wishlistService.addtoCart(product).subscribe(()=>{
            alert("Added to cart successfully");
        });
    }
}
