import{Injectable} from '@angular/core';
import{HttpClient} from '@angular/common/http';
import { IProduct } from './IProduct';
import { IWish } from './IWish';
import { UserService } from './user.service';
import { Observable,of } from 'rxjs';
import { switchMap } from 'rxjs/operators';

@Injectable({
    providedIn:'root'
})
export class WishlistService{
    private url="http://localhost:8080";
    constructor(private http:HttpClient,private userservice: UserService){}
    userid:number=0;

    addtoWishlist(product: IProduct): Observable<any> {
        return this.userservice.loggedinuser().pipe(
            switchMap((user) => {
                if (user) {
                    const wishItem = {
                        userid: user.userid,
                        productid: product.productid,
                        prodimg:product.prodimg,
                    };
                    console.log(wishItem);
                    return this.http.post<any>(`${this.url}/wishlist/add`, wishItem);
                } else {
                    return of(null); // Handle the case when the user is not logged in
                }
            })
        );
    }

    getWishlistItems(userid:number):Observable<IWish[]>{
        return this.http.get<IWish[]>(`${this.url}/wishlist/${userid}`);
    }

    removeWishlistItem(userid:number,productid:number):Observable<void>{
        return this.http.delete<any>(`${this.url}/wishlist/delete/${userid}/${productid}`);
    }

    addtoCart(wishItem: IWish): Observable<any> {
        return this.userservice.loggedinuser().pipe(
            switchMap((user) => {
                if (user) {
                    const cartItem = {
                        userid: user.userid,
                        productid: wishItem.productid,
                    };
                    console.log(cartItem);
                    return this.http.post<any>(`${this.url}/wishlist/addtocart`, cartItem);
                } else {
                    return of(null); // Handle the case when the user is not logged in
                }
            })
        );
    }
}
